                      and mes    = pMes
                      and id_trabajador = recHistorico2.id_trabajador
                      and id_historico_quincena = recHistorico2.id_historico_quincena
                      and id_concepto_tipo_personal = recHistorico2.id_concepto_tipo_personal;
               end if;
            end loop;
	    close curconceptoaporte;
        end loop; 
        close curHistorico2; 
      return 0;
end;  $_$;


