    LANGUAGE plpgsql
    AS $_$
DECLARE
       feFecha ALIAS FOR $1;
       inMeses ALIAS FOR $2;
       feFechFin       DATE;
       sbIntervalo     INTERVAL;
       interval__units VARCHAR;
 BEGIN
       interval__units := 'month';
       sbIntervalo := ('''' || inMeses || ' ' ||
