    LANGUAGE plpgsql
    AS $_$
DECLARE
       feFecha ALIAS FOR $1;
       inAnios ALIAS FOR $2;
       feFechFin       DATE;
       sbIntervalo     INTERVAL;
       interval__units VARCHAR;
