	totalMontoG float8 := 0; -- proyectado fijos (E)
	totalUnidades float8 := 0;
	aniosCumple int4 := 0;	
	diasBono float8 := 0;		
	otrasAlicuotas float8 := 0;
	bonoSinAlicuota float8 := 0;
	totalBono float8 := 0;
	montoVariable float8 := 0;
	bonoExtra float8 := 0;
	mes int4 := 0;
	dividir int4 := 0;	
	montoPetrolero float8 := 0;
	montoBonoVacacional float8 := 0;
	fechaFin date;
        pFecha date;
