        recDotacionExiste record;
	curDotacionExiste cursor (pIdTrabajador int4, pIdSubTipoDotacion int4, 
                                  pAnio int4,pMes int4)
		for SELECT de.id_dotacion_entregada as id FROM dotacionentregada de 
