                           AND de.id_subtipo_dotacion = pIdSubTipoDotacion
                           AND de.anio = pAnio
                           AND de.mes  = pMes;

  


begin

        

	IF pAccion = 'A' THEN
                
                idDotacionEntregada := last_id_dotacionentregada();
		IF pIdSubTipoDotacion = 0 THEN
                  
                   OPEN curDotacionTipo(PidTipoPersonal,pIdTipoDotacion);
