		              recFideicomisoMes.monto_Fideicomiso:=0;
	                   end if;
		           close curFideicomisoMes;

                           
                           montoPrestaciones:= (recprestacionesmensuales.monto_prestaciones 
                                              + recprestacionesmensuales.monto_adicional);
                           
